mapper.select:
SELECT
FROM scuser WHERE 1 = 1
AND NAME = #{name,jdbcType=VARCHAR}

SELECT gender, count(gender) num FROM (SELECT CASE Gender WHEN 'M' THEN '男' WHEN 'F' THEN '女' ELSE '未知' END gender FROM scuser WHERE 1=1
AND NAME = #{name,jdbcType=VARCHAR}
) t GROUP BY gender

SELECT bir,count(bir) num FROM (SELECT CASE left(birthday, 3) WHEN '199' THEN '90后' WHEN '198' THEN '80后' WHEN '197' THEN '70后' WHEN '196' THEN '60后' WHEN '195' THEN '50后' ELSE ' 其他' END bir FROM scuser WHERE 1 = 1
AND NAME = #{name,jdbcType=VARCHAR}
) t GROUP BY bir ORDER BY bir DESC

SELECT province name, count(province) value FROM (SELECT p.province, CASE WHEN p.province = '北京' THEN p.area ELSE p.city END city FROM scuser s LEFT JOIN place p ON s.District4 = p.areaID WHERE 1 = 1
AND NAME = #{name,jdbcType=VARCHAR}
AND s.District4 != '' AND s.District4 != '-' AND LENGTH(s.district4) = 6 AND p.area != '') t GROUP BY province;

SELECT city name, count(city) value FROM (SELECT p.province, CASE WHEN p.province = '北京' THEN p.area ELSE p.city END city FROM scuser s LEFT JOIN place p ON s.District4 = p.areaID WHERE 1 = 1
AND NAME = #{name,jdbcType=VARCHAR}
AND s.District4 != '' AND s.District4 != '-' AND LENGTH(s.district4) = 6 AND p.area != '') t WHERE 1 = 1
AND t.province = #{province,jdbcType=VARCHAR}
GROUP BY city;
